// web/frontend/src/components/ConfigManager.jsx
// COMPREHENSIVE: Full form with all fields + complete automation pipeline
import React, { useState, useEffect } from 'react';
import {
  Settings, Plus, RefreshCw, Trash2, Loader,
  CheckCircle, AlertCircle, FileCode, Hammer,
  ChevronRight, X, Sparkles, Code, Zap, Rocket,
  ChevronDown, ChevronUp
} from 'lucide-react';
import api from '../services/api';

const ConfigManager = () => {
  const [configs, setConfigs] = useState([]);
  const [selectedConfig, setSelectedConfig] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [generatingMachine, setGeneratingMachine] = useState(null);
  const [expandedSections, setExpandedSections] = useState({
    basic: true,
    mutation: false,
    variants: false
  });

  // Complete form state matching the JSON structure
  const [newConfig, setNewConfig] = useState({
    vulnerability_id: '',
    name: '',
    category: '',
    difficulty_range: [1, 5],
    description: '',
    variants: [''],
    entry_points: [''],
    mutation_axes: {
      filters: {
        basic: [''],
        medium: [''],
        advanced: ['']
      },
      contexts: [''],
      sinks: [''],
      encoding: [''],
      output_contexts: []
    },
    variant_configs: []
  });

  useEffect(() => {
    loadConfigs();
  }, []);

  const loadConfigs = async () => {
    try {
      setLoading(true);
      const data = await api.getConfigs();
      setConfigs(data);
    } catch (error) {
      showMessage('Failed to load configs: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const showMessage = (text, type = 'info') => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 8000);
  };

  const toggleSection = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const handleCreateConfig = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);
      showMessage('ðŸš€ Starting full pipeline: Config â†’ Blueprint â†’ Mutations â†’ Templates â†’ Machine...', 'info');

      // Clean up empty strings from arrays
      const cleanConfig = {
        ...newConfig,
        variants: newConfig.variants.filter(v => v.trim()),
        entry_points: newConfig.entry_points.filter(ep => ep.trim()),
        mutation_axes: {
          filters: {
            basic: newConfig.mutation_axes.filters.basic.filter(f => f.trim()),
            medium: newConfig.mutation_axes.filters.medium.filter(f => f.trim()),
            advanced: newConfig.mutation_axes.filters.advanced.filter(f => f.trim())
          },
          contexts: newConfig.mutation_axes.contexts.filter(c => c.trim()),
          sinks: newConfig.mutation_axes.sinks.filter(s => s.trim()),
          encoding: newConfig.mutation_axes.encoding.filter(e => e.trim()),
          output_contexts: newConfig.mutation_axes.output_contexts.filter(oc => oc.trim())
        },
        variant_configs: newConfig.variant_configs
      };

      // Step 1: Create config
      showMessage('ðŸ“ Step 1/5: Creating config...', 'info');
      const configResult = await api.createConfig(cleanConfig);

      // Step 2: Generate blueprint
      showMessage('ðŸŽ¯ Step 2/5: Generating blueprint...', 'info');
      await api.generateFromConfig(cleanConfig.category);

      // Step 3: Run generator.py (mutations)
      showMessage('âš™ï¸ Step 3/5: Running generator.py (creating mutations)...', 'info');
      await api.runGenerator(cleanConfig.category);

      // Step 4: Run template_engine.py
      showMessage('ðŸ”§ Step 4/5: Running template_engine.py (generating templates)...', 'info');
      await api.runTemplateEngine(cleanConfig.category);

      // Step 5: Generate machine
      showMessage('ðŸš€ Step 5/5: Building Docker machine...', 'info');
      const machineResult = await api.generateMachineFromConfig(cleanConfig.category);

      showMessage(
        `ðŸŽ‰ SUCCESS! Complete machine ready! ID: ${machineResult.machine_id} | URL: ${machineResult.url || 'http://localhost:8080'}`,
