// web/frontend/src/components/ConfigManager.jsx
import React, { useState, useEffect } from 'react';
import api from '../services/api';

const ConfigManager = () => {
  const [configs, setConfigs] = useState([]);
  const [selectedConfig, setSelectedConfig] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);

  // Form state for new config
  const [newConfig, setNewConfig] = useState({
    vulnerability_id: '',
    name: '',
    category: '',
    difficulty_range: [1, 5],
    description: '',
    variants: [''],
    entry_points: [''],
    mutation_axes: {
      filters: {
        basic: [''],
        medium: [''],
        advanced: ['']
      },
      contexts: [''],
      sinks: [''],
      encoding: ['none'],
      output_contexts: ['']
    },
    variant_configs: []
  });

  useEffect(() => {
    loadConfigs();
  }, []);

  const loadConfigs = async () => {
    try {
      setLoading(true);
      const data = await api.getConfigs();
      setConfigs(data);
    } catch (error) {
      showMessage('Failed to load configs: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const showMessage = (text, type = 'info') => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 5000);
  };

  const handleCreateConfig = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      // Clean up empty strings from arrays
      const cleanConfig = {
        ...newConfig,
        variants: newConfig.variants.filter(v => v.trim()),
        entry_points: newConfig.entry_points.filter(ep => ep.trim()),
        mutation_axes: {
          ...newConfig.mutation_axes,
          filters: {
            basic: newConfig.mutation_axes.filters.basic.filter(f => f.trim()),
            medium: newConfig.mutation_axes.filters.medium.filter(f => f.trim()),
            advanced: newConfig.mutation_axes.filters.advanced.filter(f => f.trim())
          },
          contexts: newConfig.mutation_axes.contexts.filter(c => c.trim()),
          sinks: newConfig.mutation_axes.sinks.filter(s => s.trim()),
          encoding: newConfig.mutation_axes.encoding.filter(e => e.trim()),
          output_contexts: newConfig.mutation_axes.output_contexts.filter(oc => oc.trim())
        }
      };

      const result = await api.createConfig(cleanConfig);
      showMessage('Config created successfully!', 'success');
      setShowCreateForm(false);
      loadConfigs();

      // Reset form
      setNewConfig({
        vulnerability_id: '',
        name: '',
        category: '',
        difficulty_range: [1, 5],
        description: '',
        variants: [''],
        entry_points: [''],
        mutation_axes: {
          filters: { basic: [''], medium: [''], advanced: [''] },
          contexts: [''],
          sinks: [''],
          encoding: ['none'],
          output_contexts: ['']
        },
        variant_configs: []
      });

    } catch (error) {
      showMessage('Failed to create config: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateBlueprint = async (category) => {
    try {
      setLoading(true);
      await api.generateFromConfig(category);
      showMessage(`Blueprint generated from ${category} config!`, 'success');
    } catch (error) {
      showMessage('Failed to generate blueprint: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateAll = async () => {
    try {
      setLoading(true);
      const result = await api.generateAllConfigs();
      showMessage(`Generated ${result.generated?.length || 0} blueprints!`, 'success');

      if (result.failed && result.failed.length > 0) {
        console.error('Failed configs:', result.failed);
      }
    } catch (error) {
      showMessage('Failed to generate blueprints: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteConfig = async (category) => {
    if (!window.confirm(`Delete config: ${category}?`)) return;

    try {
      setLoading(true);
      await api.deleteConfig(category);
      showMessage('Config deleted successfully', 'success');
      loadConfigs();
    } catch (error) {
      showMessage('Failed to delete config: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const addArrayItem = (path, value = '') => {
    setNewConfig(prev => {
      const newState = { ...prev };
      const parts = path.split('.');
      let current = newState;

      for (let i = 0; i < parts.length - 1; i++) {
        current = current[parts[i]];
      }

      const lastKey = parts[parts.length - 1];
      current[lastKey] = [...current[lastKey], value];

      return newState;
    });
  };

  const updateArrayItem = (path, index, value) => {
    setNewConfig(prev => {
      const newState = { ...prev };
      const parts = path.split('.');
      let current = newState;

      for (let i = 0; i < parts.length - 1; i++) {
        current = current[parts[i]];
      }

      const lastKey = parts[parts.length - 1];
      const newArray = [...current[lastKey]];
      newArray[index] = value;
      current[lastKey] = newArray;

      return newState;
    });
  };

  const removeArrayItem = (path, index) => {
    setNewConfig(prev => {
      const newState = { ...prev };
      const parts = path.split('.');
      let current = newState;

      for (let i = 0; i < parts.length - 1; i++) {
        current = current[parts[i]];
      }

      const lastKey = parts[parts.length - 1];
      current[lastKey] = current[lastKey].filter((_, i) => i !== index);

      return newState;
    });
  };

  return (
    <div className="p-6">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2">Vulnerability Config Manager</h1>
        <p className="text-gray-600">Create and manage vulnerability configuration files</p>
      </div>

      {message && (
        <div className={`mb-4 p-4 rounded ${
          message.type === 'error' ? 'bg-red-100 text-red-700' : 
          message.type === 'success' ? 'bg-green-100 text-green-700' : 
          'bg-blue-100 text-blue-700'
        }`}>
          {message.text}
        </div>
      )}

      <div className="mb-6 flex gap-4">
        <button
          onClick={() => setShowCreateForm(!showCreateForm)}
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          {showCreateForm ? 'Cancel' : '+ Create New Config'}
        </button>

        <button
          onClick={handleGenerateAll}
          disabled={loading || configs.length === 0}
          className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 disabled:bg-gray-400"
        >
          üî® Generate All Blueprints
        </button>

        <button
          onClick={loadConfigs}
          disabled={loading}
          className="bg-gray-600 text-white px-6 py-2 rounded hover:bg-gray-700"
        >
          üîÑ Refresh
        </button>
      </div>

      {showCreateForm && (
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <h2 className="text-2xl font-bold mb-4">Create New Config</h2>

          <form onSubmit={handleCreateConfig} className="space-y-6">
            {/* Basic Info */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium mb-2">Vulnerability ID</label>
                <input
                  type="text"
                  required
                  value={newConfig.vulnerability_id}
                  onChange={(e) => setNewConfig({...newConfig, vulnerability_id: e.target.value})}
                  className="w-full px-3 py-2 border rounded"
                  placeholder="e.g., xss_001"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">Category</label>
                <input
                  type="text"
                  required
                  value={newConfig.category}
                  onChange={(e) => setNewConfig({...newConfig, category: e.target.value})}
                  className="w-full px-3 py-2 border rounded"
                  placeholder="e.g., cross_site_scripting"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Name</label>
              <input
                type="text"
                required
                value={newConfig.name}
                onChange={(e) => setNewConfig({...newConfig, name: e.target.value})}
                className="w-full px-3 py-2 border rounded"
                placeholder="e.g., Cross-Site Scripting"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Description</label>
              <textarea
                required
                value={newConfig.description}
                onChange={(e) => setNewConfig({...newConfig, description: e.target.value})}
                className="w-full px-3 py-2 border rounded"
                rows="3"
                placeholder="Describe the vulnerability..."
              />
            </div>

            {/* Variants */}
            <div>
              <label className="block text-sm font-medium mb-2">Variants</label>
              {newConfig.variants.map((variant, index) => (
                <div key={index} className="flex gap-2 mb-2">
                  <input
                    type="text"
                    value={variant}
                    onChange={(e) => updateArrayItem('variants', index, e.target.value)}
                    className="flex-1 px-3 py-2 border rounded"
                    placeholder="e.g., Reflected XSS"
                  />
                  <button
                    type="button"
                    onClick={() => removeArrayItem('variants', index)}
                    className="px-3 py-2 bg-red-500 text-white rounded"
                  >
                    ‚úï
                  </button>
                </div>
              ))}
              <button
                type="button"
                onClick={() => addArrayItem('variants')}
                className="text-blue-600 hover:underline"
              >
                + Add Variant
              </button>
            </div>

            {/* Entry Points */}
            <div>
              <label className="block text-sm font-medium mb-2">Entry Points</label>
              {newConfig.entry_points.map((ep, index) => (
                <div key={index} className="flex gap-2 mb-2">
                  <input
                    type="text"
                    value={ep}
                    onChange={(e) => updateArrayItem('entry_points', index, e.target.value)}
                    className="flex-1 px-3 py-2 border rounded"
                    placeholder="e.g., http_get_param"
                  />
                  <button
                    type="button"
                    onClick={() => removeArrayItem('entry_points', index)}
                    className="px-3 py-2 bg-red-500 text-white rounded"
                  >
                    ‚úï
                  </button>
                </div>
              ))}
              <button
                type="button"
                onClick={() => addArrayItem('entry_points')}
                className="text-blue-600 hover:underline"
              >
                + Add Entry Point
              </button>
            </div>

            {/* Contexts */}
            <div>
              <label className="block text-sm font-medium mb-2">Contexts</label>
              {newConfig.mutation_axes.contexts.map((context, index) => (
                <div key={index} className="flex gap-2 mb-2">
                  <input
                    type="text"
                    value={context}
                    onChange={(e) => updateArrayItem('mutation_axes.contexts', index, e.target.value)}
                    className="flex-1 px-3 py-2 border rounded"
                    placeholder="e.g., comment_section"
                  />
                  <button
                    type="button"
                    onClick={() => removeArrayItem('mutation_axes.contexts', index)}
                    className="px-3 py-2 bg-red-500 text-white rounded"
                  >
                    ‚úï
                  </button>
                </div>
              ))}
              <button
                type="button"
                onClick={() => addArrayItem('mutation_axes.contexts')}
                className="text-blue-600 hover:underline"
              >
                + Add Context
              </button>
            </div>

            {/* Filters - Basic */}
            <div>
              <label className="block text-sm font-medium mb-2">Basic Filters</label>
              {newConfig.mutation_axes.filters.basic.map((filter, index) => (
                <div key={index} className="flex gap-2 mb-2">
                  <input
                    type="text"
                    value={filter}
                    onChange={(e) => updateArrayItem('mutation_axes.filters.basic', index, e.target.value)}
                    className="flex-1 px-3 py-2 border rounded"
                    placeholder="e.g., script_tag"
                  />
                  <button
                    type="button"
                    onClick={() => removeArrayItem('mutation_axes.filters.basic', index)}
                    className="px-3 py-2 bg-red-500 text-white rounded"
                  >
                    ‚úï
                  </button>
                </div>
              ))}
              <button
                type="button"
                onClick={() => addArrayItem('mutation_axes.filters.basic')}
                className="text-blue-600 hover:underline"
              >
                + Add Basic Filter
              </button>
            </div>

            <div className="flex gap-4">
              <button
                type="submit"
                disabled={loading}
                className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 disabled:bg-gray-400"
              >
                {loading ? 'Creating...' : 'Create Config'}
              </button>

              <button
                type="button"
                onClick={() => setShowCreateForm(false)}
                className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Configs List */}
      <div className="bg-white rounded-lg shadow">
        <div className="px-6 py-4 border-b">
          <h2 className="text-xl font-bold">Available Configs ({configs.length})</h2>
        </div>

        {loading && configs.length === 0 ? (
          <div className="p-6 text-center">Loading configs...</div>
        ) : configs.length === 0 ? (
          <div className="p-6 text-center text-gray-500">
            No configs found. Create one to get started!
          </div>
        ) : (
          <div className="divide-y">
            {configs.map((config) => (
              <div key={config.filename} className="p-6 hover:bg-gray-50">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold">{config.name}</h3>
                    <p className="text-sm text-gray-600 mt-1">
                      ID: {config.vulnerability_id} | Category: {config.category}
                    </p>
                    <p className="text-sm text-gray-500 mt-1">
                      {config.variants_count} variant(s)
                    </p>
                  </div>

                  <div className="flex gap-2">
                    <button
                      onClick={() => handleGenerateBlueprint(config.category)}
                      disabled={loading}
                      className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 disabled:bg-gray-400"
                    >
                      üî® Generate Blueprint
                    </button>

                    <button
                      onClick={() => handleDeleteConfig(config.category)}
                      disabled={loading}
                      className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 disabled:bg-gray-400"
                    >
                      üóëÔ∏è Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ConfigManager;
