import React, { useState, useEffect } from 'react';
import {
  Settings, Plus, RefreshCw, Trash2, Loader,
  CheckCircle, AlertCircle, FileCode, Hammer,
  X, Code, Rocket, Zap, MinusCircle
} from 'lucide-react';

// Mock API - replace with your actual API import
const api = {
  getConfigs: async () => [],
  createConfig: async (config) => config,
  createConfigWithMachine: async (config) => ({ ...config, auto_generated: true }),
  generateMachineFromConfig: async (category) => ({ machine_id: 'test', url: 'http://localhost:8080' }),
  generateFromConfig: async (category) => ({}),
  generateAllConfigs: async () => ({ generated: [] }),
  deleteConfig: async (category) => ({})
};

const ConfigManager = () => {
  const [configs, setConfigs] = useState([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);

  const [newConfig, setNewConfig] = useState({
    vulnerability_id: '',
    name: '',
    category: '',
    difficulty_range: [1, 5],
    description: '',
    variants: [''],
    entry_points: [''],
    mutation_axes: {
      filters: {
        basic: [''],
        medium: [''],
        advanced: ['']
      },
      contexts: [''],
      sinks: [''],
      encoding: ['none'],
      output_contexts: ['']
    },
    variant_configs: []
  });

  useEffect(() => {
    loadConfigs();
  }, []);

  const loadConfigs = async () => {
    try {
      setLoading(true);
      const data = await api.getConfigs();
      setConfigs(data);
    } catch (error) {
      showMessage('Failed to load configs: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const showMessage = (text, type = 'info') => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 8000);
  };

  const handleSubmit = async (autoGenerate = true) => {
    try {
      setLoading(true);

      const cleanConfig = {
        ...newConfig,
        variants: newConfig.variants.filter(v => v.trim()),
        entry_points: newConfig.entry_points.filter(ep => ep.trim()),
        mutation_axes: {
          ...newConfig.mutation_axes,
          filters: {
            basic: newConfig.mutation_axes.filters.basic.filter(f => f.trim()),
            medium: newConfig.mutation_axes.filters.medium.filter(f => f.trim()),
            advanced: newConfig.mutation_axes.filters.advanced.filter(f => f.trim())
          },
          contexts: newConfig.mutation_axes.contexts.filter(c => c.trim()),
          sinks: newConfig.mutation_axes.sinks.filter(s => s.trim()),
          encoding: newConfig.mutation_axes.encoding.filter(e => e.trim()),
          output_contexts: newConfig.mutation_axes.output_contexts.filter(oc => oc.trim())
        },
        variant_configs: newConfig.variant_configs.filter(vc => vc.name.trim())
      };

      if (autoGenerate) {
        await api.createConfigWithMachine(cleanConfig);
        showMessage(`ðŸŽ‰ Config created and machine generated!`, 'success');
      } else {
        await api.createConfig(cleanConfig);
        showMessage('Config created successfully!', 'success');
      }

      setShowCreateForm(false);
      loadConfigs();
      resetForm();

    } catch (error) {
      showMessage('Failed to create config: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setNewConfig({
      vulnerability_id: '',
      name: '',
      category: '',
      difficulty_range: [1, 5],
      description: '',
      variants: [''],
      entry_points: [''],
      mutation_axes: {
        filters: { basic: [''], medium: [''], advanced: [''] },
        contexts: [''],
        sinks: [''],
        encoding: ['none'],
        output_contexts: ['']
      },
      variant_configs: []
    });
  };

  const addArrayItem = (path, value = '') => {
    setNewConfig(prev => {
      const newState = JSON.parse(JSON.stringify(prev));
      const parts = path.split('.');
      let current = newState;

      for (let i = 0; i < parts.length - 1; i++) {
        current = current[parts[i]];
      }

      const lastKey = parts[parts.length - 1];
      current[lastKey].push(value);
      return newState;
    });
  };

  const updateArrayItem = (path, index, value) => {
    setNewConfig(prev => {
      const newState = JSON.parse(JSON.stringify(prev));
      const parts = path.split('.');
      let current = newState;

      for (let i = 0; i < parts.length - 1; i++) {
        current = current[parts[i]];
      }

      const lastKey = parts[parts.length - 1];
      current[lastKey][index] = value;
      return newState;
    });
  };

  const removeArrayItem = (path, index) => {
    setNewConfig(prev => {
      const newState = JSON.parse(JSON.stringify(prev));
      const parts = path.split('.');
      let current = newState;

      for (let i = 0; i < parts.length - 1; i++) {
        current = current[parts[i]];
      }

      const lastKey = parts[parts.length - 1];
      current[lastKey].splice(index, 1);
      return newState;
    });
  };

  const addVariantConfig = () => {
    setNewConfig(prev => ({
      ...prev,
      variant_configs: [
        ...prev.variant_configs,
        { name: '', parameters: [{ name: '', axis: '', default: '' }] }
      ]
    }));
  };

  const updateVariantConfig = (vcIndex, field, value) => {
    setNewConfig(prev => {
      const newState = JSON.parse(JSON.stringify(prev));
      newState.variant_configs[vcIndex][field] = value;
      return newState;
    });
  };

  const addVariantParameter = (vcIndex) => {
    setNewConfig(prev => {
      const newState = JSON.parse(JSON.stringify(prev));
      newState.variant_configs[vcIndex].parameters.push({ name: '', axis: '', default: '' });
      return newState;
    });
  };

  const updateVariantParameter = (vcIndex, paramIndex, field, value) => {
    setNewConfig(prev => {
      const newState = JSON.parse(JSON.stringify(prev));
      newState.variant_configs[vcIndex].parameters[paramIndex][field] = value;
      return newState;
    });
  };

  const removeVariantParameter = (vcIndex, paramIndex) => {
    setNewConfig(prev => {
      const newState = JSON.parse(JSON.stringify(prev));
      newState.variant_configs[vcIndex].parameters.splice(paramIndex, 1);
      return newState;
    });
  };

  const removeVariantConfig = (vcIndex) => {
    setNewConfig(prev => ({
      ...prev,
      variant_configs: prev.variant_configs.filter((_, i) => i !== vcIndex)
    }));
  };

  const ArrayInput = ({ label, path, values, placeholder = '' }) => (
    <div>
      <label className="block text-sm font-medium text-gray-400 mb-2">{label}</label>
      <div className="space-y-2">
        {values.map((value, index) => (
          <div key={index} className="flex gap-2">
            <input
              type="text"
              value={value}
              onChange={(e) => updateArrayItem(path, index, e.target.value)}
              className="flex-1 px-4 py-2 bg-black/50 border border-gray-800 rounded-lg text-white focus:outline-none focus:border-orange-500"
              placeholder={placeholder}
            />
            {values.length > 1 && (
              <button
                onClick={() => removeArrayItem(path, index)}
                className="p-2 bg-red-600/20 hover:bg-red-600/30 text-red-400 rounded-lg"
              >
                <MinusCircle className="w-5 h-5" />
              </button>
            )}
          </div>
        ))}
        <button
          onClick={() => addArrayItem(path, '')}
          className="px-4 py-2 bg-gray-800 hover:bg-gray-700 text-white rounded-lg flex items-center gap-2"
        >
          <Plus className="w-4 h-4" />
          Add {label}
        </button>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2 bg-gradient-to-r from-white via-orange-500 to-orange-600 bg-clip-text text-transparent">
            Vulnerability Configs
          </h1>
          <p className="text-gray-400">Create configs and generate ready-to-use machines</p>
        </div>

        {message && (
          <div className={`mb-6 p-4 rounded-2xl border flex items-center gap-3 ${
            message.type === 'error' ? 'bg-red-950/20 border-red-500/50' :
            message.type === 'success' ? 'bg-green-950/20 border-green-500/50' :
            'bg-blue-950/20 border-blue-500/50'
          }`}>
            {message.type === 'error' ? <AlertCircle className="w-5 h-5 text-red-500" /> :
             message.type === 'success' ? <CheckCircle className="w-5 h-5 text-green-500" /> :
             <Loader className="w-5 h-5 text-blue-500 animate-spin" />}
            <span>{message.text}</span>
          </div>
        )}

        <div className="mb-8 flex gap-4">
          <button
            onClick={() => setShowCreateForm(!showCreateForm)}
            className="px-6 py-3 bg-orange-500 hover:bg-orange-600 text-white rounded-xl flex items-center gap-2"
          >
            {showCreateForm ? <X className="w-5 h-5" /> : <Plus className="w-5 h-5" />}
            {showCreateForm ? 'Cancel' : 'Create New Config'}
          </button>
        </div>

        {showCreateForm && (
          <div className="mb-8 rounded-2xl border border-gray-900 bg-gradient-to-br from-gray-900/50 to-black/50 p-6">
            <h2 className="text-2xl font-bold mb-6 flex items-center gap-2">
              <FileCode className="w-6 h-6 text-orange-500" />
              Create New Config
            </h2>

            <div className="space-y-6">
              {/* Basic Info */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-400 mb-2">Vulnerability ID *</label>
                  <input
                    type="text"
                    value={newConfig.vulnerability_id}
                    onChange={(e) => setNewConfig({...newConfig, vulnerability_id: e.target.value})}
                    className="w-full px-4 py-3 bg-black/50 border border-gray-800 rounded-xl text-white focus:outline-none focus:border-orange-500"
                    placeholder="e.g., sqli_001"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-400 mb-2">Name *</label>
                  <input
                    type="text"
                    value={newConfig.name}
                    onChange={(e) => setNewConfig({...newConfig, name: e.target.value})}
                    className="w-full px-4 py-3 bg-black/50 border border-gray-800 rounded-xl text-white focus:outline-none focus:border-orange-500"
                    placeholder="e.g., SQL Injection"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-400 mb-2">Category *</label>
                  <input
                    type="text"
                    value={newConfig.category}
                    onChange={(e) => setNewConfig({...newConfig, category: e.target.value})}
                    className="w-full px-4 py-3 bg-black/50 border border-gray-800 rounded-xl text-white focus:outline-none focus:border-orange-500"
                    placeholder="e.g., sql_injection"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-400 mb-2">Difficulty Range</label>
                  <div className="flex gap-2">
                    <input
                      type="number"
                      min="1" max="5"
                      value={newConfig.difficulty_range[0]}
                      onChange={(e) => setNewConfig({
                        ...newConfig,
                        difficulty_range: [parseInt(e.target.value), newConfig.difficulty_range[1]]
                      })}
                      className="w-full px-4 py-3 bg-black/50 border border-gray-800 rounded-xl text-white focus:outline-none focus:border-orange-500"
                    />
                    <span className="text-gray-500 flex items-center">to</span>
                    <input
                      type="number"
                      min="1" max="5"
                      value={newConfig.difficulty_range[1]}
                      onChange={(e) => setNewConfig({
                        ...newConfig,
                        difficulty_range: [newConfig.difficulty_range[0], parseInt(e.target.value)]
                      })}
                      className="w-full px-4 py-3 bg-black/50 border border-gray-800 rounded-xl text-white focus:outline-none focus:border-orange-500"
                    />
                  </div>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Description *</label>
                <textarea
                  value={newConfig.description}
                  onChange={(e) => setNewConfig({...newConfig, description: e.target.value})}
                  className="w-full px-4 py-3 bg-black/50 border border-gray-800 rounded-xl text-white focus:outline-none focus:border-orange-500"
                  rows={3}
                  placeholder="Describe the vulnerability..."
                />
              </div>

              <ArrayInput
                label="Variants"
                path="variants"
                values={newConfig.variants}
                placeholder="e.g., Error-based SQLi"
              />

              <ArrayInput
                label="Entry Points"
                path="entry_points"
                values={newConfig.entry_points}
                placeholder="e.g., login_form"
              />

              {/* Mutation Axes */}
              <div className="space-y-4 p-4 border border-gray-800 rounded-xl">
                <h3 className="text-lg font-bold text-orange-500">Mutation Axes</h3>

                <ArrayInput
                  label="Filters - Basic"
                  path="mutation_axes.filters.basic"
                  values={newConfig.mutation_axes.filters.basic}
                  placeholder="e.g., single_quote"
                />

                <ArrayInput
                  label="Filters - Medium"
                  path="mutation_axes.filters.medium"
                  values={newConfig.mutation_axes.filters.medium}
                  placeholder="e.g., union_keyword"
                />

                <ArrayInput
                  label="Filters - Advanced"
                  path="mutation_axes.filters.advanced"
                  values={newConfig.mutation_axes.filters.advanced}
                  placeholder="e.g., comment_syntax"
                />

                <ArrayInput
                  label="Contexts"
                  path="mutation_axes.contexts"
                  values={newConfig.mutation_axes.contexts}
                  placeholder="e.g., login_authentication"
                />

                <ArrayInput
                  label="Sinks"
                  path="mutation_axes.sinks"
                  values={newConfig.mutation_axes.sinks}
                  placeholder="e.g., select_query"
                />

                <ArrayInput
                  label="Encoding"
                  path="mutation_axes.encoding"
                  values={newConfig.mutation_axes.encoding}
                  placeholder="e.g., url_encoding"
                />

                <ArrayInput
                  label="Output Contexts"
                  path="mutation_axes.output_contexts"
                  values={newConfig.mutation_axes.output_contexts}
                  placeholder="e.g., error_message"
                />
              </div>

              {/* Variant Configs */}
              <div className="space-y-4 p-4 border border-gray-800 rounded-xl">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-bold text-orange-500">Variant Configurations</h3>
                  <button
                    onClick={addVariantConfig}
                    className="px-4 py-2 bg-orange-500 hover:bg-orange-600 text-white rounded-lg flex items-center gap-2"
                  >
                    <Plus className="w-4 h-4" />
                    Add Variant
                  </button>
                </div>

                {newConfig.variant_configs.map((vc, vcIndex) => (
                  <div key={vcIndex} className="p-4 bg-black/30 border border-gray-700 rounded-lg space-y-3">
                    <div className="flex items-center justify-between">
                      <input
                        type="text"
                        value={vc.name}
                        onChange={(e) => updateVariantConfig(vcIndex, 'name', e.target.value)}
                        className="flex-1 px-4 py-2 bg-black/50 border border-gray-800 rounded-lg text-white focus:outline-none focus:border-orange-500"
                        placeholder="Variant name (e.g., Error-based SQLi)"
                      />
                      <button
                        onClick={() => removeVariantConfig(vcIndex)}
                        className="ml-2 p-2 bg-red-600/20 hover:bg-red-600/30 text-red-400 rounded-lg"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>

                    <div className="space-y-2">
                      <label className="text-sm font-medium text-gray-400">Parameters</label>
                      {vc.parameters.map((param, paramIndex) => (
                        <div key={paramIndex} className="flex gap-2">
                          <input
                            type="text"
                            value={param.name}
                            onChange={(e) => updateVariantParameter(vcIndex, paramIndex, 'name', e.target.value)}
                            className="flex-1 px-3 py-2 bg-black/50 border border-gray-800 rounded-lg text-white focus:outline-none focus:border-orange-500"
                            placeholder="Parameter name"
                          />
                          <input
                            type="text"
                            value={param.axis}
                            onChange={(e) => updateVariantParameter(vcIndex, paramIndex, 'axis', e.target.value)}
                            className="flex-1 px-3 py-2 bg-black/50 border border-gray-800 rounded-lg text-white focus:outline-none focus:border-orange-500"
                            placeholder="Axis"
                          />
                          <input
                            type="text"
                            value={param.default}
                            onChange={(e) => updateVariantParameter(vcIndex, paramIndex, 'default', e.target.value)}
                            className="flex-1 px-3 py-2 bg-black/50 border border-gray-800 rounded-lg text-white focus:outline-none focus:border-orange-500"
                            placeholder="Default value"
                          />
                          <button
                            onClick={() => removeVariantParameter(vcIndex, paramIndex)}
                            className="p-2 bg-red-600/20 hover:bg-red-600/30 text-red-400 rounded-lg"
                          >
                            <MinusCircle className="w-4 h-4" />
                          </button>
                        </div>
                      ))}
                      <button
                        onClick={() => addVariantParameter(vcIndex)}
                        className="px-3 py-1.5 bg-gray-800 hover:bg-gray-700 text-white rounded-lg text-sm flex items-center gap-1"
                      >
                        <Plus className="w-3 h-3" />
                        Add Parameter
                      </button>
                    </div>
                  </div>
                ))}
              </div>

              <div className="flex gap-4 pt-4">
                <button
                  onClick={() => handleSubmit(true)}
                  disabled={loading}
                  className="px-8 py-3 bg-orange-500 hover:bg-orange-600 disabled:bg-gray-800 disabled:opacity-50 text-white rounded-xl flex items-center gap-2"
                >
                  {loading ? (
                    <>
                      <Loader className="w-5 h-5 animate-spin" />
                      Creating...
                    </>
                  ) : (
                    <>
                      <Rocket className="w-5 h-5" />
                      Create & Generate Machine
                    </>
                  )}
                </button>

                <button
                  onClick={() => setShowCreateForm(false)}
                  className="px-8 py-3 bg-gray-800 hover:bg-gray-700 text-white rounded-xl"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Configs List */}
        <div className="rounded-2xl border border-gray-900 bg-gradient-to-br from-gray-900/50 to-black/50 overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-800">
            <h2 className="text-xl font-bold flex items-center gap-2">
              <Code className="w-5 h-5 text-orange-500" />
              Available Configs ({configs.length})
            </h2>
          </div>

          {configs.length === 0 ? (
            <div className="p-12 text-center">
              <Settings className="w-16 h-16 text-gray-700 mx-auto mb-4" />
              <h3 className="text-xl font-bold text-gray-600 mb-2">No Configs Found</h3>
              <p className="text-gray-500">Create your first vulnerability config to get started</p>
            </div>
          ) : (
            <div className="divide-y divide-gray-800">
              {configs.map((config, index) => (
                <div key={index} className="p-6 hover:bg-gray-900/30">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-lg font-bold">{config.name}</h3>
                      <p className="text-sm text-gray-500">{config.category}</p>
                    </div>
                    <div className="flex gap-2">
                      <button className="px-4 py-2 bg-orange-600/20 text-orange-400 rounded-xl flex items-center gap-2">
                        <Rocket className="w-4 h-4" />
                        Generate
                      </button>
                      <button className="px-4 py-2 bg-red-600/20 text-red-400 rounded-xl">
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ConfigManager;
